CREATE TABLE "public"."events" ("id" text NOT NULL, "name" text NOT NULL, "date" timestamptz NOT NULL, "ticketsTotal" integer NOT NULL, "ticketsSold" integer NOT NULL, PRIMARY KEY ("id") , UNIQUE ("id"), CONSTRAINT "ticketsTotalGreaterThanZero" CHECK ("ticketsTotal" > 0), CONSTRAINT "ticketsSoldGreaterOrEqualZero" CHECK ("ticketsSold" >= 0), CONSTRAINT "ticketsSoldLessOrEqualTicketsTotal" CHECK ("ticketsSold" <= "ticketsTotal"));
